        select * from ProductPreview
		left join Product
		on ProductPreview.productId=Product.id
		left join Category
		on Product.categoryId = Category.id


		    	 Category.superCategoryId=#{superCategoryId}


		    	 Category.id=#{categoryId}


  		order by ProductPreview.regDate desc ;
