mapper.select:
		select pp.*, if ((select memberId from WishList where pp.productId = productId and memberId = #{memberId}) = #{memberId}, 1, 0) status from ProductPreview pp 
       where regMemberId = #{memberId}
       order by regDate desc;

    	select pp.thumbNailImg,pp.price,wl.regDate,pp.productId,pp.name,pp.regMemberId,pp.salesStatusId, if(wl.memberId = #{memberId},1,0) status 
		from ProductPreview pp join WishList wl on pp.productId = wl.productId where memberId = #{memberId}
        order by regDate desc;

select * from ProductPreview
where productId = #{productId}
order by regDate desc ;

select * from ProductPreview left join Product on ProductPreview.productId=Product.id left join Category on Product.categoryId = Category.id
Category.superCategoryId=#{superCategoryId}
Category.id=#{categoryId}
